import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component{

  state={
    profesores:[]
  }

componentDidMount(){
  this.getProfesores();
}

getProfesores = _ => {
  fetch("http://localhost:4000/profesores")
    .then(response=>response.json())
    .then(response=>this.setState({profesores:response.data}))
    .catch(err => console.error(err))
}

renderProfesores=({nombre,rol}) => <div key={nombre}>Profesor: {nombre} <br/>Rol: {rol}</div>

  render(){
    const {profesores}=this.state;
    return(
      <div className="App">
        {profesores.map(this.renderProfesores)}
      </div>
    );
  }
}

export default App;
