import React from "react";
//import './App.css'

import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";

class MenuAlumno extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            usuario: localStorage.getItem("Sesion")
        }
    }

    handleClick() {
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <h1>Sistema de Registro de Protocolos </h1>
                </header>
                <div className="bienvenida">
                    <h1>Bienvenido {this.state.usuario}</h1>
                </div>
                <Router>
                    <div className='container'>
                        <button>
                            <Link to="/registrar">Registrar Protocolo</Link>
                        </button>
                        <div classname="separador"></div>
                        <br></br>
                        <button>
                            <Link to="/edoProtocolo">Estado de Protocolo</Link>
                        </button>
                        <div classname="separador"></div>
                        <br></br>
                        <button>
                            <Link to="/evaluacion">Evaluación de Protocolo</Link>
                        </button>
                        <div classname="separador"></div>
                        <br></br>
                        <button>
                            <Link to="/unirse">Unirse a Protocolo</Link>
                        </button>
                        <div classname="separador"></div>
                        <br></br>

                    </div>
                    <Switch>
                        <Route path="/registrar">
                            <Registrar />
                        </Route>
                        <Route path="/edoProtocolo">
                            <EdoProtocolo />
                        </Route>
                        <Route path="/evaluacion">
                            <Evaluacion />
                        </Route>
                        <Route path="/unirse">
                            <Unirse />
                        </Route>
                    </Switch>
                </Router>

            </div>
        );
    }
}

function Registrar() {
    return (
        <div>
            <h1>Registrar Protocolo</h1>
            <h2>Nombre de Trabajo Terminal</h2>
            <input type='nombreTT'
                placeholder='               Nombre TT' />
            <br></br>
            <h2>Palabras Clave</h2>
            <input type='palabrasClave'
                placeholder='            Palabras Clave' />
            <br></br>
            <h2>Link a Trabajo Terminal</h2>
            <input type='linkTT'
                placeholder='Ejemplo: http://drive.nombreTT.com"' />
        </div>
    );
}
function EdoProtocolo() {
    return (
        <div>
            <h1>Estado de TT</h1>
            <h2>Su protocolo actualmente se encuentra:</h2>

        </div>
    );
}
function Evaluacion() {
    return (
        <div>
            <h1>Evaluación de TT</h1>
            <h2>La calificación de su trabajo es:</h2>

        </div>
    );
}
function Unirse() {
    return (
        <div>
            <h1>¿Desea unirse a un protocolo?</h1>
            <h2>Ingrese el número del protocolo</h2>
            <input type='numTT'
                placeholder='             Número TT' />
        </div>
    );
}
export default MenuAlumno;
