import React from "react";
//import './App.css'

import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";

class MenuAlumno extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            usuario: localStorage.getItem("Sesion")
        }
    }

    cleanSession() {
        localStorage.removeItem("Sesion")
        localStorage.removeItem("Type")
    }

    handleClick() {
    }

    render() {
        return (
            <div className="container">
                <header className="App-header">
                    <h1>Sistema de Registro de Protocolos </h1>
                </header>
                <div className="bienvenida">
                    <h1>Bienvenido {this.state.usuario}</h1>
                </div>
                <Router>
                    <div className='container'>
                        <button>
                            <Link to="/Alumno/Bienvenido/registrar">Registrar Protocolo</Link>
                        </button>
                        <div classname="separador"></div>
                        <br></br>
                        <button>
                            <Link to="/Alumno/Bienvenido/edoProtocolo">Estado de Protocolo</Link>
                        </button>
                        <div classname="/Alumno/Bienvenido/separador"></div>
                        <br></br>
                        <button>
                            <Link to="/Alumno/Bienvenido/evaluacion">Evaluaci√≥n de Protocolo</Link>
                        </button>
                        <div classname="separador"></div>
                        <br></br>
                        <button>
                            <Link to="/Alumno/Bienvenido/unirse">Unirse a Protocolo</Link>
                        </button>
                        <div classname="separador"></div>
                        <br></br>

                    </div>
                    <Switch>
                        <Route path="/Alumno/Bienvenido/registrar">
                            <Registrar />
                        </Route>
                        <Route path="/Alumno/Bienvenido/edoProtocolo">
                            <EdoProtocolo />
                        </Route>
                        <Route path="/Alumno/Bienvenido/evaluacion">
                            <Evaluacion />
                        </Route>
                        <Route path="/Alumno/Bienvenido/unirse">
                            <Unirse />
                        </Route>
                    </Switch>
                </Router>
                <Link to="/sistematt"><button className="myButton" onClick={this.cleanSession}>Log Out</button></Link>
            </div>
        );
    }
}

function Registrar() {
    return (
        // <div className="container">
        //     <h1>Registrar Protocolo</h1>
        //     <h2>Nombre de Trabajo Terminal</h2>
        //     <input type='nombreTT'
        //         placeholder='               Nombre TT' />
        //     <br></br>
        //     <h2>Palabras Clave</h2>
        //     <input type='palabrasClave'
        //         placeholder='            Palabras Clave' />
        //     <br></br>
        //     <h2>Link a Trabajo Terminal</h2>
        //     <input type='linkTT'
        //         placeholder='Ejemplo: http://drive.nombreTT.com"' />
        // </div>
        <div className="row">
            <div className="col-md-6 mt-5 mx-auto">
                <form noValidate>
                    <h1 className="h3 mb-3 font-weight-normal">Registro</h1>
                    <div className="form-group">
                        <label htmlFor="nombre">Nombre</label>
                        <input
                            type="text"
                            className="form-control"
                            name="nombre"
                            placeholder="Ingresa tu nombre"
                            // value={this.state.nombre}
                            // onChange={this.handleChange1}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="usuario">Boleta</label>
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Ingresa tu boleta"
                            // value={this.state.usuario}
                            // onChange={this.handleChange2}
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="contrase√±a">Contrase√±a</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Ingresa una contrase√±a"
                            //value={this.state.contrase√±a}
                            //onChange={this.handleChange3}
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="rol">Correo</label><br />
                        <input
                            type="email"
                            className="form-control"
                            placeholder="Ingresa un correo electr√≥nico."
                            //value={this.state.correo}
                           // onChange={this.handleChange4}
                        />
                    </div>
                    <button
                        type="submit"
                        className="btn btn-lg btn-primary btn-block"
                        //onClick={this.handleSubmit}
                    >
                        Registrate!
              </button>
                </form>
            </div>
        </div>
    );
}
function EdoProtocolo() {
    return (
        <div className="container">
            <h1>Estado de TT</h1>
            <h2>Su protocolo actualmente se encuentra:</h2>

        </div>
    );
}
function Evaluacion() {
    return (
        <div className="container">
            <h1>Evaluaci√≥n de TT</h1>
            <h2>La calificaci√≥n de su trabajo es:</h2>

        </div>
    );
}
function Unirse() {
    return (
        <div className="container">
            <h1>¬øDesea unirse a un protocolo?</h1>
            <h2>Ingrese el n√∫mero del protocolo</h2>
            <input type='numTT'
                placeholder='             N√∫mero TT' />
        </div>
    );
}
export default MenuAlumno;
