import React from 'react'
import '../profesor.css'
import '../button.css'
import '../formulario.css'
import { Link } from 'react-router-dom';
import NavBar from './Navbar'

class Profesor extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            usuario: '',
            contraseña: ''
        }
        this.handleChange1 = this.handleChange1.bind(this)
        this.handleChange2 = this.handleChange2.bind(this)
        this.login = this.login.bind(this)
    }

    handleChange1(event) {
        this.setState({ usuario: event.target.value })
    }

    handleChange2(event) {
        this.setState({ contraseña: event.target.value })
    }

    async login() {
        const response = await fetch(`http://localhost:4000/profesores/login?usuario=${this.state.usuario}&contraseña=${this.state.contraseña}`)
        const json = await response.json();
        if (json.validate == 1) {
            window.location.href = "/MenuProfesores"
        }
        else {
            alert("El usuario o la contraseña son incorrectos.")
            window.location.href = "/Profesor"
        }
    }

    render() {
        return (
            <div className="container">
                <NavBar />
                {/* <br /><div>Bienvenido</div><br />
                <form class="form">
                    <h2>Iniciar Sesión</h2>
                    <p type="Usuario:"><input placeholder="Usuario" value={this.state.usuario} onChange={this.handleChange1}></input></p>
                    <p type="Contraseña"><input type="password" placeholder="Contraseña" value={this.state.contraseña} onChange={this.handleChange2}></input></p>
                    <p><input type="submit" classname="button" onClick={this.login} value="Iniciar Sesión"/></p>
                </form> */}
                <div className="row">
                    <div className="col-md-6 mt-5 mx-auto">
                        <form onSubmit={this.login}>
                            <h1 className="h3 mb-3 font-weight-normal">Inicia Sesion</h1>
                            <div className="form-group">
                                <label htmlFor="correo">Usuario</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    placeholder="Ingresa tu correo"
                                    value={this.state.usuario}
                                    onChange={this.handleChange1}
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="contraseña">Contraseña</label>
                                <input
                                    type="password"
                                    className="form-control"
                                    placeholder="Contraseña"
                                    value={this.state.contraseña}
                                    onChange={this.handleChange2}
                                />
                            </div>
                            <button
                            type="submit"
                            className="btn btn-lg btn-primary btn-block"
                            onClick={this.login}
                            >Iniciar Sesión
                        </button>
                        </form>
                        
                    </div>
                </div>
            </div>
        );
    }
}

export default Profesor;
