  //     method: "post",
  //     headers: {
  //       "Accept": "application/json",
  //       "Content-type": "application/json",
  //     },
  //     body: JSON.stringify({
  //       usuario: this.state.usuario,
  //     })
  //   })
  //     .catch(err => console.error(err))
  //   const json = await response.json();
  //   this.setState({ protocolos: json.data })

  //   var that = this
  //   var a = d3.selectAll("td").selectAll("div").each(function () {
  //     d3.select(this)
  //       .text(this.getAttribute("id"))
  //       .on("click", d => {
  //         that.setState({ seleccion: this.getAttribute("id") })
  //       })
  //   })
  // }

  // renderProtocolos = ({ Numero, Nombre, Estatus }) =>
  //   <tr>
  //     <td>
  //       <Link to="/Profesor/Bienvenido/Evaluacion"><div id={Numero}></div></Link>
  //     </td>
  //     <td>
  //       {Nombre}
  //     </td>
  //     <td>
  //       {Estatus}
  //     </td>
  //   </tr>

  render() {
    if (this.state.usuario == null) {
      window.location.href = "/Profesor"
    }

    return (
      <div className="container">
        <header className="App-header">
          <h1>Sistema de Registro de Protocolos </h1>
        </header>
        <div className="bienvenida"><br />
          <h1>Bienvenido, {this.state.usuario}</h1>
        </div>
        {/* <h1>Trabajos Terminales Asignados</h1><br />
        <div className="container">
          <h1>Protocolos asignados</h1>
          <div>
            <table className="tabla" align="center">
              <tr>
                <th>NumeroTT</th>
                <th>NombreTT</th>
                <th>Estatus</th>
              </tr>
              {this.state.protocolos.map(this.renderProtocolos)}
            </table>
          </div>
        </div> */}
        <Switch>
          <Route path="/Profesor/Bienvenido/Evaluacion" component={() => <Evaluacion protocolo={this.state.seleccion} />} />
          <Route path="/Profesor/Bienvenido/Asignados" component={()=><Asignados usaurio={this.state.usuario}/>}/>
        </Switch>
        <br />
        <Link to="/Profesor/Bienvenido/Asignados">Asignados</Link>
        <Link to="/sistematt"><button className="myButton" onClick={this.cleanSession}>Log Out</button></Link>
      </div>
    )
  }
}

export default MenuProfesor;
