import React from 'react'
import '../profesor.css'
import '../button.css'
import '../formulario.css'
import { Link } from 'react-router-dom';

class Profesor extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            usuario: '',
            contraseña: '',
            validate: null
        }
        this.handleChange1 = this.handleChange1.bind(this)
        this.handleChange2 = this.handleChange2.bind(this)
    }

    handleChange1(event) {
        this.setState({ usuario: event.target.value })
    }

    handleChange2(event) {
        this.setState({ contraseña: event.target.value })
    }

    login = _ => {
        
        //const {profesores} = this.state;
        // const request= async ()=>{const response =await fetch(`http://localhost:4000/profesores/login?usuario=${this.state.usuario}&contraseña=${this.state.contraseña}`)
        // .catch(err => console.error(err))
        // .then(response => response.json())
        // .then(response => this.setState({ validate: response.validate }))
        // const json= await response.json();
        // }
        const request = async () => {
            const response = await fetch(`http://localhost:4000/profesores/login?usuario=${this.state.usuario}&contraseña=${this.state.contraseña}`);
            const json= await response.json();
            console.log(json)
            this.setState({validate:json})
        }
        
        request();

        if(this.state.validate==1){
            alert("Bienvenido")
        }
        else{
            alert("No estás registrado")
        }
        
    }

    render() {
        return (
            <div className="container">
                <br /><div>Bienvenido</div><br />
                <form class="form">
                    <h2>Iniciar Sesión</h2>
                    <p type="Usuario:"><input placeholder="Usuario" value={this.state.usuario} onChange={this.handleChange1}></input></p>
                    <p type="Contraseña"><input type="password" placeholder="Contraseña" value={this.state.contraseña} onChange={this.handleChange2}></input></p>
                </form>
                <button onClick={this.login}>Iniiar sesion</button>
                <br />
                <div className="formdiv">
                    <span class="fa fa-phone"></span>57296000 Ext. 52007
                        <span class="fa fa-envelope-o"></span> sescolares_escom@ipn.mx
                    </div>
                <br /><br />
                <Link to="/sistematt"><button className="myButton">Menu Principal</button></Link>
            </div>
        );
    }
}

export default Profesor;
