import React from "react";
import '../button.css'
import {Link} from "react-router-dom";

class MenuProfesor extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      protocolos: [],
      usuario: localStorage.getItem("Sesion")
    }
  }

  cleanSession() {
    localStorage.removeItem("Sesion")
    localStorage.removeItem("Type")
  }

  componentDidMount() {
    this.getProtocolos();
  }

  getProtocolos = _ => {
    fetch("http://localhost:4000/ProtocolosProfe", {
      method: "post",
      headers: {
        "Accept": "application/json",
        "Content-type": "application/json",
      },
      body: JSON.stringify({
        usuario: this.state.usuario,
      })
    })
      .catch(err => console.error(err))
      .then(response => response.json())
      .then(response => this.setState({ protocolos: response.data }))

  }

  Evalua(Número){
    alert(Número)
  }

  renderProtocolos = ({ Número, Nombre, Estatus }) =>
    <tr>
      <td>
       <Link onClick={this.Evalua(Número)}>{Número}</Link>  
      </td>
      <td>
        {Nombre}
      </td>
      <td>
        {Estatus}
      </td>
    </tr>

  render() {
    if (this.state.usuario == null) {
      window.location.href = "/Profesor"
    }

    return (
      <div className="container">
        <header className="App-header">
          <h1>Sistema de Registro de Protocolos </h1>
        </header>
        <div className="bienvenida"><br />
          <h1>Bienvenido, {this.state.usuario}</h1>
        </div>
        <h1>Trabajos Terminales Asignados</h1><br />
        <div className="container">
          <h1>Protocolos asignados</h1>
          <div>
            <table className="tabla" align="center">
              <tr>
                <th>NumeroTT</th>
                <th>NombreTT</th>
                <th>Estatus</th>
              </tr>
              {this.state.protocolos.map(this.renderProtocolos)}
              {/* <tr>
          <td>Nombre del TT:</td>
          <td>Aquí va el nombre del TT</td>
        </tr>
        <tr>
          <td>Alumno a cargo:</td>
          <td>Aquí va el nombre del alumno</td>
        </tr>
        <tr>
          <td>Link a protocolo:</td>
          <td>Aquí va el link</td>
        </tr>
        <tr>
          <td>Subir evaluación:</td>
          <td>Aquí va la evaluación</td>
        </tr>
        <tr>
          <td>Observaciones:</td>
          <td>                                                                                                                             Aquí van las observaciones</td>
        </tr> */}
            </table>
          </div>
        </div>
        {/* <Router>
          <div className='container'>
            <button>
              <Link to="/ttasignado">Trabajos Terminales asignados</Link>
            </button>
            <div classname="separador"></div>
          </div>
          <Switch>
            <Route path="/ttasignado">
              <TTasignado />
            </Route>
          </Switch>
        </Router> */}
        <br/>
        <Link to="/sistematt"><button className="myButton" onClick={this.cleanSession}>Log Out</button></Link>
      </div>
    )
  }
}

export default MenuProfesor;
