import React from 'react'

class EstadoProtocolo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            usuario: this.props.usuario,
            evaluaciones: []
        }
    }

    componentDidMount() {
        this.protocolos()
    }

    renderProtocolos = ({ Profesor, Estatus }) =>
        <tr>
            <td>
                {Profesor}
            </td>
            <td>
                {Estatus}
            </td>
        </tr>

    async protocolos() {
        const response = await fetch("http://192.168.0.18:4000/EvaluacionesAlumno", {
            method: "post",
            headers: {
                "Accept": "application/json",
                "Content-type": "application/json",
            },
            body: JSON.stringify({
                boleta: this.state.usuario,
            })
        })
            .catch(err => console.error(err))
        const json = await response.json();
        this.setState({ evaluaciones: json.data })
    }

    render() {
        return (
            <div className="container">
                <div>
                    <h1>Estado de TT</h1>
                    <h2>Evaluaciones de su protocolo:</h2>
                    <table className="tabla" align="center">
                        <tr>
                            <th>Profesor</th>
                            <th>Estatus</th>
                        </tr>
                        
                    </table>
                </div>
            </div >
        );
    }
}

export default EstadoProtocolo
