import React from 'react'
import '../button.css'
import * as d3 from 'd3'

class RegistrarProtocolo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            usuario: this.props.usuario,
            cont: 1,
            palabrasClave: []
        }
        this.incrementa = this.incrementa.bind(this);
    }

    incrementa() {
        this.setState({ cont: this.state.cont + 1 })
        d3.select("div.palabras")
            .append("input")
            .attr("type", "text")
            .attr("placeHolder", "            Palabra clave " + ((this.state.cont) + 1));
    }

    render() {
        return (
            <div>
                <h1>Registrar Protocolo</h1>
                <h2>Nombre de Trabajo Terminal</h2>
                <input type='nombreTT'
                    placeholder='               Nombre TT' />
                <br></br>
                <h2>Palabras Clave</h2>

                <button className="myButton" onClick={this.incrementa}>Añadir Palabra Clave</button>
                <div className="palabras">
                    <input type='palabrasClave'
                        placeholder='            Palabra clave 1' />
                    {/* Aquí van las palabras clave */}
                </div>
                <br></br>
                <h2>Link a PDF</h2>
                <input type='linkTT'
                    placeholder='Ejemplo: http://drive.nombreTT.com"' />
            </div>
        );
    }
}

export default RegistrarProtocolo
