import React from 'react'
import * as d3 from 'd3'
import {Link} from 'react-router-dom'

class ProtocolosAsignados extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            protocolos: [],
            usuario: localStorage.getItem("Sesion"),
            seleccion: ''
        }
    }

    async getProtocolos() {
        const response = await fetch("http://localhost:4000/ProtocolosProfe", {
            method: "post",
            headers: {
                "Accept": "application/json",
                "Content-type": "application/json",
            },
            body: JSON.stringify({
                usuario: this.state.usuario,
            })
        })
            .catch(err => console.error(err))
        const json = await response.json();
        this.setState({ protocolos: json.data })

        var that = this
        var a = d3.selectAll("td").selectAll("div").each(function () {
            d3.select(this)
                .text(this.getAttribute("id"))
                .on("click", d => {
                    that.setState({ seleccion: this.getAttribute("id") })
                })
        })
    }

    renderProtocolos = ({ Numero, Nombre, Estatus }) =>
        <tr>
            <td>
                <Link to="/Profesor/Bienvenido/Evaluacion"><div id={Numero}></div></Link>
            </td>
            <td>
                {Nombre}
            </td>
            <td>
                {Estatus}
            </td>
        </tr>

    render() {
        return (
            <div>

                <h1>Trabajos Terminales Asignados</h1><br />
                <div className="container">
                    <h1>Protocolos asignados</h1>
                    <div>
                        <table className="tabla" align="center">
                            <tr>
                                <th>NumeroTT</th>
                                <th>NombreTT</th>
                                <th>Estatus</th>
                            </tr>
                            {this.state.protocolos.map(this.renderProtocolos)}
                        </table>
                    </div>
                    <Link to="/Profesor/Evaluacion/Bienvenido">Menu</Link>
                </div>
            </div>
        )
    }
}

export default ProtocolosAsignados
