import React from 'react'
import '../profesor.css'
import '../button.css'
import '../formulario.css'
import { Link } from 'react-router-dom';

class Profesor extends React.Component {

    state = {
        profesores: [],
        usuario: null,
        contraseña: null
    }

    componentDidMount() {
        this.getProfesores();
    }

    handleChange1(event) {
        this.setState({ usuario:event.target.value })
      }
    
      handleChange2(event) {
        this.setState({ contraseña:event.target.value })
      }  

    getProfesores() {
        fetch("http://localhost:4000/profesores")
            .catch(err => console.error(err))
            .then(response => response.json())
            .then(response => this.setState({ profesores: response.data }))
    }

    handleSubmit(){
        const {profesores}=this.state;
        profesores.map(this.renderProfesores)
    }

    renderProfesores=({usuario,contraseña})=>
        {if(this.state.usuario==usuario && this.state.contraseña==contraseña){
            window.location.href="/AlumnoMenu/"
        }else{
            window.location.href="/Profesor/"
        }}

    render() {
        return (
            <div className="container">
                <br /><div>Bienvenido</div><br />
                <form class="form" onSubmit={this.handleSubmit}>
                    <h2>Iniciar Sesión</h2>
                    <p type="Usuario:"><input placeholder="Escribe tu nombre" value={this.state.usuario} onChange={this.handleChange1}></input></p>
                    <p type="Contraseña"><input type="password" placeholder="Introduzca su contraseña" value={this.state.contraseña} onChange={this.handleChange2}></input></p>

                    <button onClick >Iniciar Sesión</button>


                </form>
                <br />
                <div className="formdiv">
                    <span class="fa fa-phone"></span>57296000 Ext. 52007
                        <span class="fa fa-envelope-o"></span> sescolares_escom@ipn.mx
                    </div>
                <br /><br />
                <Link to="/sistematt"><button className="myButton">Menu Principal</button></Link>
            </div>
        );
    }
}

export default Profesor;
