import axios from 'axios'

export const register = nuevoAlumno => {
  return axios
    .post('alumnos/register', {
      nombre: nuevoAlumno.nombre,
      boleta: nuevoAlumno.boleta,
      usuario: nuevoAlumno.usuario,
      correo: nuevoAlumno.correo,
      contraseña: nuevoAlumno.contraseña,
      
    })
    .then(response => {
      console.log('Registrado')
    })
}

export const login = alumno => {
  return axios
    .post('alumnos/login', {
      correo: alumno.correo,
      contraseña: alumno.contraseña
    })
    .then(response => {
      localStorage.setItem('usertoken', response.data)
      return response.data
    })
    .catch(err => {
      console.log(err)
    })
}
